import { useState } from 'react';
//import './style.css';


function Home() {
  return (
    <div>
      <SearchBar/>
    </div>
  )
}

function SearchBar() {
  const [search, setSearch] = useState(""); 

  function searchProduct() {
    let searchInput= document.getElementById("searchFor").value

    setSearch(searchInput);
  }
  return (
    <div className="searchFor">

      <a href="#">Servibras</a>

      <div>
        <input id="searchFor" type="text" placeholder="Busque o seu produto"/>
        <button onClick={searchProduct}>Procurar</button>
      </div>

      <div>Resultado: {search}</div>
    </div>
  )
}

export default Home
