import { useState } from 'react';
//import './style.css';


function Home() {
  return (
    <div>
      <h1>Home...</h1>
      <SearchBar/>
    </div>
  )
}

function SearchBar() {
  const [search, setSearch] = useState(""); 

  function searchProduct() {
    let searchInput= document.getElementById("searchFor").value

    setSearch(searchInput);
  }
  return (
    <div className="searchFor" style={{background: "pink"}}>

      <input id="searchFor" 
      style={{background: "orange", borderRadius: "0.6rem"}} 
      type="text" placeholder="Pesquise aqui o seu produto..."/>

      <button onClick={searchProduct} 
      style={{background: "yellow"}}>Pesquisar</button>

      <br/><br/><br/>
      <div>Resultado: {search}</div>
    </div>
  )
}

export default Home
